import { Component } from '@angular/core';
import { Persona } from '.';

@Component({
  selector: 'app-bucles',
  templateUrl: './bucles.component.html',
  styleUrls: ['./bucles.component.css']
})
export class BuclesComponent {

  personas: Persona[] = [
    { nombre: 'Juan', edad: 20 },
    { nombre: 'Ana', edad: 25 },
    { nombre: 'Luis', edad: 30 },
    { nombre: 'María', edad: 35 }
  ];

}
<div>

    <h2>Bucles</h2>

    <ul>
        <li *ngFor="let persona of personas, let i = index">
            {{ i + 1 }} {{ '-' + persona.nombre + ' (' + persona.edad + ')' }}
        </li>
    </ul>

</div>
